import React, { useState } from "react";
import "./App.css";
import Card from "./Componentes/Card";
import Primera from "./Componentes/primera2.jpg";
import Segunda from "./Componentes/segunda.jpg";
import CardDias from "./Componentes/CardDias";

function App() {
  const [texto1, setTexto1] = useState([]);
  const [texto2, setTexto2] = useState("");
  const [texto3, setTexto3] = useState("");
  const [texto4, setTexto4] = useState("");

  const addEjercicio = () => {
    const clon = [...texto1];
    clon.push({
      texto2,
      texto3,
      texto4
    });
    setTexto1(clon);
  };

  const ejercicios = [
    {
      texto1: "Bicicleta",
      texto2: "Eliptico",
      texto3: "Cinta",
      texto4: "Abdominales",
      texto5: "Sentadillas",
      texto6: "Estocadas"
    }
  ];
  const imagenes = [
    {
      img: Primera,
      title: "LUNES",
      color: "#00FFFF"
    },
    {
      img: Segunda,
      title: "Martes",
      color: "#00FFFF"
    }
  ];

  return (
    <div>
      <Card
        setTexto1={SetTexto1}
        setTexto2={SetTexto2}
        setTexto3={SetTexto3}
        setTexto4={SetTexto4}
      ></Card>
      <div className="cards-container">
        {imagenes.map(card => {
          return (
            <CardDias
              title={card.title}
              color={card.color}
              img={card.img}
              texto1={card.texto1}
              texto2={card.texto2}
              texto3={card.texto3}
            ></CardDias>
          );
        })}
      </div>
    </div>
  );
}

export default App;
