import React, { useState } from "react";
import "./App.css";
import CardDias from "./Componentes/CardDias";
import Inputs from "./Componentes/Inputs";
import Checkbox from "./Componentes/Check";


function App() {
  const [ejercicios,setEjercicios]=useState([]);
  const [ejercicio1,setEjercicio1]=useState("");
  const [ejercicio2,setEjercicio2]=useState("");
  const [ejercicio3,setEjercicio3]=useState("");
  const [ejercicio4,setEjercicio4]=useState("");
  const [ejercicio5,setEjercicio5]=useState("");
  const [ejercicio6,setEjercicio6]=useState("");
  const [rutinas, setRutinas]=useState([
    {
      day:"Lunes",
      actividad: [
        {
          name:"Abdominales",
          repeticiones:"20"
        }
      ]
    }
  ]);
  const [dias,setDias]=useState("");
  const [repeticiones,setRepeticiones]=useState("");

  const addEjercicios=()=>{
    setRutinas([...rutinas,{
      day:"Miercoles",
      actividad: [
        {
          name:"",
          repeticiones:""
        }
      ]
    }])
    const clon=[...ejercicios];
    clon.push({
      ejercicio1,
      ejercicio2,
      ejercicio3,
      ejercicio4,
      ejercicio5,
      ejercicio6
    })
  }

  return (
    <div>
            <Inputs
              setEjercicio1={setEjercicio1}
              setEjercicio2={setEjercicio2}
              setEjercicio3={setEjercicio3}
              setEjercicio4={setEjercicio4}
              setEjercicio5={setEjercicio5}
              setEjercicio6={setEjercicio6}
            ></Inputs>
      <div className="cards-container">
      {rutinas.map(card => {
        return (
          <CardDias
          title={card.day}
          actividad={card.actividad}
          ></CardDias>
          );
        })}
        <Checkbox></Checkbox>
        </div>
        <button onClick={addEjercicios}>Guardar</button>

    </div>
  );
}

export default App;
