import React, { useState } from "react";
import "./App.css";
import CardDias from "./Componentes/CardDias";
import Inputs from "./Componentes/Inputs";
import Header from "./Section/Header";

function App() {

  const [rutinas, setRutinas] = useState([]);


  const [ejercicios,setEjercicios] = useState([]);

  const agregarEjercicio = (e) =>{
    const {name,value} = e.target;
    if(value){
      const clon = [...ejercicios];
      if(!clon[dia]) clon[dia] = [];
      const index= clon[dia].findIndex(ej=>ej.ejercicio===name);
      if(index === -1) {
        clon[dia].push({ejercicio:name,repeticiones:value});
      }else {
        clon[dia][index] = {ejercicio:name,repeticiones:value};
      }

      setEjercicios(clon);
    }
  }

  return (
    <div>
      <Header></Header>
      <div className="input-container">
      <Inputs
        agregarEjercicio={agregarEjercicio}
      ></Inputs>
      </div>
      <div className="cards-container">
        {rutinas.map(card => {
          return (
            <CardDias key={card.day} day={card.day} actividad={card.actividad}></CardDias>
          );
        })}
      </div>
      <button onClick={addEjercicios}>Guardar</button>
    </div>
  );
}

export default App;
